import React from 'react';
import Autosuggest from 'react-autosuggest';
import reactDom from 'react-dom';

class BasicAutoSuggest extends React.Component {
    constructor() {
        super();
        this.state = {
            value: '',
            suggestions:[],
        };
    }

    cars = [
        {
            name: 'Suzuki',
            orgin: 'Japan',
            year: 1909
        },
        {
            name: 'Fiat',
            origin: 'Italy',
            year: 1899
        },
        {
            name: 'Honda',
            origin: 'Japan',
            year: 1948
        },
        {
            name: 'Porch',
            origin: 'Germany',
            year: 1954
        },
        {
            name: 'Audi',
            origin: 'Germany',
            year: 1910
        }
    ];

    getSuggestions = value => {
        const inputValue = value.trim().toLowerCase();
        const inputLength = inputValue.length;

        return inputLength === 0 ? [] : this.cars.filter(lang =>
            lang.name.toLowerCase().slice(0, inputLength) === inputValue
        );
    };

    getSuggestionValue = suggestion => suggestion.name;

    rendersuggestion = suggestion => {
        <div>
            {suggestion.name} | Founded in {suggestion.year}, {suggestion.origin}
        </div>
    };

    onChange = (event, { newValue }) => {
        this.setState({
            value: newValue
        });
    };

    onSuggestionFetchRequested = ({ value }) => {
        this.setState({
            suggestions: this.getSuggestions(value)
        });
    };

    onSuggestionClearRequested = () => {
        this.setState({
            suggestions: []
        });
    };

    render() {
        const { value, suggestions } = this.state;
        const inputProps = {
            placeholder: 'Type car name',
            value,
            onChange: this.onChange
        };
        return (
            <Autosuggest
                suggestions = {suggestions}
                onSuggestionFetchRequested = {this.onSuggestionFetchRequested}
                onSuggestionClearRequested = {this.onSuggestionClearRequested}
                getSuggestionValue = {this.getSuggestionValue}
                renderSuggestion = {this.renderSuggestion}
                inputProps = {inputProps}
            />
        );
    }
}

export default BasicAutoSuggest;
