import React from 'react';
import Autosuggest from 'react-autosuggest';
import './autosuggest.css';

class ServerAutoSuggest extends React.Component {
    constructor() {
        super();

        this.state = {
            value: '',
            suggestions: []
        };
    }

    getSuggestions = async (value) => {
        const inputValue = value.trim().toLowerCase();
        if (inputValue.length >= 3) {
            var response = await fetch("https://restcountries.eu/rest/v2/name/" + inputValue + "?fields=name;region;population;languages;currencies");
            var data = await response.json();
        }
        return data;
    };

    getSuggestionValue = suggestion => suggestion.name;

    renderSuggestion = suggestion => (
        <span className="sugg-option">
            <span className="name">
                {suggestion.name} | {suggestion.region} | {suggestion.population}
            </span>
        </span>
    );

    onChange = (event, { newValue }) => {
        this.setState({
            value:newValue
        });
    };

    onSuggestionsFetchRequested = ({ value }) => {
        this.getSuggestions(value)
        .then(data => {
            if (data.Error) {
                this.setState({
                    suggestions: []
                });
            } else {
                this.setState({
                    suggestions: data
                });
            }
        })
    };

    onSuggestionsClearRequested = () => {
        this.setState({
            suggestions: []
        });
    };

    render() {
        const { value, suggestions } = this.state;
        const inputProps = {
            placeholder: 'Type country name',
            value,
            onChange: this.onChange
        };

        return (
            <Autosuggest
                suggestions = {suggestions}
                onSuggestionsFetchRequested = {this.onSuggestionsFetchRequested}
                onSuggestionsClearRequested = {this.onSuggestionsClearRequested}
                getSuggestionValue = {this.getSuggestionValue}
                renderSuggestion = {this.renderSuggestion}
                inputProps = {inputProps}
            />
        );
    }
}

export default ServerAutoSuggest;
